CREATE TABLE IF NOT EXISTS moviedb (
    `genres` VARCHAR(199) CHARACTER SET utf8,
    `homepage` VARCHAR(93) CHARACTER SET utf8,
    `id` INT,
    `imdb_id` VARCHAR(9) CHARACTER SET utf8,
    `original_language` VARCHAR(2) CHARACTER SET utf8,
    `original_title` VARCHAR(54) CHARACTER SET utf8,
    `overview` VARCHAR(955) CHARACTER SET utf8,
    `popularity` NUMERIC(9, 6),
    `poster_path` VARCHAR(32) CHARACTER SET utf8,
    `release_date` DATETIME,
    `runtime` INT,
    `tagline` VARCHAR(89) CHARACTER SET utf8,
    `vote_average` NUMERIC(2, 1),
    `vote_count` INT
);
